import { Component } from '@angular/core';
import {HttpClient} from "@angular/common/http";

interface HikingTrail {
  name: string;
  difficulty: number;
  distance: number;
  duration: number;
  latitude: number;
  longitude: number;
}

interface ApiTrack {
  name: string;
  difficulty: number;
  distance: number;
  duration: number;
}

@Component({
  selector: 'app-hikes',
  templateUrl: './hikes.component.html',
  styleUrls: ['./hikes.component.css']
})
export class HikesComponent {
  difficulty = 1;
  latitude = 0;
  longitude = 0;
  maxDistance = 0;
  trails: HikingTrail[] = [];

  constructor(private http: HttpClient) {}

  onSubmit(): void {
    this.http.get<{tracks: ApiTrack[]}>(`https://mapzs.pzs.si/api/search?trailTypes=1&difficulty=${this.difficulty}`)
        .subscribe(data => {
          console.log(data)
          this.trails = data.tracks.map((track: ApiTrack) => {
            // Assign random latitude and longitude values
            const latitude = Math.random() * (46.8769 - 45.4215) + 45.4215;
            const longitude = Math.random() * (16.6105 - 13.3753) + 13.3753;
            return {
              name: track.name,
              difficulty: track.difficulty,
              distance: track.distance,
              duration: track.duration,
              latitude: latitude,
              longitude: longitude
            };
          })
              .filter((trail: HikingTrail) => {
                // Calculate distance between user and trail location
                const distance = this.calculateDistance(this.latitude, this.longitude, trail.latitude, trail.longitude);
                return distance <= this.maxDistance;
              });
        });
  }

  calculateDistance(lat1:number, lon1:number, lat2:number, lon2:number) {
    const R = 6371; // Radius of the Earth in kilometers
    const dLat = this.degreesToRadians(lat2 - lat1);
    const dLon = this.degreesToRadians(lon2 - lon1);

    const a =
        Math.sin(dLat / 2) * Math.sin(dLat / 2) +
        Math.cos(this.degreesToRadians(lat1)) * Math.cos(this.degreesToRadians(lat2)) *
        Math.sin(dLon / 2) * Math.sin(dLon / 2)
    ;

    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    const distance = R * c; // Distance in kilometers

    return distance;
  }

  degreesToRadians(degrees:number) {
    return degrees * (Math.PI / 180);
  }
}
