import { Component, OnInit } from '@angular/core';
import { Dostavljalec } from 'src/app/models/dostavljalec';
import { DeliveryService } from 'src/app/services/delivery.service';
import {
  faBicycle,
  faCarSide,
  faCircleCheck,
  faCircleXmark,
} from '@fortawesome/free-solid-svg-icons';

@Component({
  selector: 'app-delivery-people',
  templateUrl: './delivery-people.component.html',
  styleUrls: ['./delivery-people.component.css'],
})
export class DeliveryPeopleComponent implements OnInit {
  faBicycle = faBicycle;
  faCarSide = faCarSide;
  faCircleCheck = faCircleCheck;
  faCircleXmark = faCircleXmark;

  constructor(private deliveryService: DeliveryService) {}

  public dostavljalec: Dostavljalec[] | undefined;

  ngOnInit(): void {
    this.deliveryService.getDeliveryPeople().subscribe(
      (data) => {
        console.log(data);
        this.dostavljalec = data;
      },
      (error) => {
        console.log('error: ', error);
      }
    );
  }
}
<h3>Naši dostavljalci</h3>

<div class="person" *ngFor="let dost of dostavljalec">
  <div class="person-name">{{ dost.firstName }} {{ dost.lastName }}</div>

  <div class="icons">
    <!-- Show transport mode -->
    <div class="transport">
      <ng-container
        class="person-other"
        *ngIf="dost.meansOfTransport === 'car'; else elseBlock"
        ><fa-icon [icon]="faCarSide"></fa-icon
      ></ng-container>
      <ng-template #elseBlock
        ><fa-icon [icon]="faBicycle"></fa-icon
      ></ng-template>
    </div>
    <!-- Show availability -->
    <div class="checkmark">
      <ng-container
        class="person-other"
        *ngIf="dost.availability === true; else another"
      >
        <fa-icon [icon]="faCircleCheck" id="check"></fa-icon>
      </ng-container>
      <ng-template #another
        ><fa-icon [icon]="faCircleXmark" id="xmark"></fa-icon
      ></ng-template>
    </div>
  </div>
</div>
