import { Component } from '@angular/core';
import {HttpClient} from "@angular/common/http";
import {faCloudSun} from "@fortawesome/free-solid-svg-icons/faCloudSun";
import {faLocationDot} from "@fortawesome/free-solid-svg-icons/faLocationDot";
import {ServiceService} from "../../services/service.service";
import {Hike} from "../../models/Hike";

interface Weather {
  temperature: number;
  humidity: number;
  wind: number;
  clouds: string;
}

@Component({
  selector: 'app-hikes',
  templateUrl: './hikes.component.html',
  styleUrls: ['./hikes.component.css']
})
export class HikesComponent {
  faCloud = faCloudSun;
  faMapMarker = faLocationDot;
  temperature: any;
  difficulty = 1;
  latitude = 0;
  longitude = 0;
  maxDistance = 0;
  trails: Hike[] = [];
  selectedTrail?: Hike; // The trail selected by the user
  weather?: Weather; // The weather data for the selected trail
  loading: boolean = false; // The loading state of the modal

  constructor(private http: HttpClient, private service: ServiceService) {}

  onSubmit(): void {
    this.service.getHikes(this.latitude, this.longitude, this.maxDistance, this.difficulty).subscribe((hikes: Hike[]) => {
      this.trails = hikes;
    });
  }

  getWeather(trail: Hike): void {
    // Set the selected trail
    //this.selectedTrail = trail;
    // Set the loading state to true
    //this.loading = true;
    // Call the weather service with the trail coordinates
    // this.weatherService.getWeather(trail.latitude, trail.longitude).subscribe(
    //     data => {
    //       // Set the weather data
    //       this.weather = data;
    //       // Set the loading state to false
    //       this.loading = false;
    //     },
    //     error => {
    //       // Handle the error
    //       console.error(error);
    //       // Set the loading state to false
    //       this.loading = false;
    //     }
    // );
    console.log(trail)

    this.service.getWeather(trail.startingLatitude, trail.startingLongitude).subscribe(res => {
      this.loading = true;
      this.temperature = res.current.temperature_2m;
      console.log(res.current.temperature_2m);
      this.loading = false;

    })
  }

  getDifficultyLabel(difficulty: number): string {
    switch (difficulty) {
      case 1:
        return 'Easy';
      case 2:
        return 'Medium';
      case 3:
        return 'Hard';
      case 4:
        return 'Extreme';
      default:
        return 'Unknown';
    }
  }

    getLocation() {
        this.service.getLocation().subscribe(res => {
          this.latitude = res.latitude;
          this.longitude = res.longtitude;
        });
    }
}
