import { Injectable } from '@angular/core';
import {HttpClient, HttpHeaders, HttpParams} from '@angular/common/http';
import { environment } from 'src/environment/environment';
import { Hike } from '../models/Hike';

@Injectable({
  providedIn: 'root',
})
export class ServiceService {
  constructor(private http: HttpClient) {}

  private hikesUrl = environment['url'] + '/hike/v1/hikes';
  private userUrl = environment['url'] + '/hiker/v1/users';
  private locationUrl = environment['url'] + '/location/v1/Locations';



  getHikes(lat: number, lon: number, distance: number, difficulty: number) {
    // Define the parameters
    let params = new HttpParams()
        .set('lat', String(lat))
        .set('lon', String(lon))
        .set('distance', String(distance))
        .set('difficulty', String(difficulty));

    // Make the GET request and return the result
    return this.http.get<Hike[]>(this.hikesUrl, { params: params });
  }

  signIn(firstName: string, lastName: string) {

    // Make the GET request and return the result
    return this.http.get<any>(this.userUrl + '/1');
  }

  createUser(firstName: string, lastName: string) {

  }

  getLocation() {
    return this.http.get<any>(this.locationUrl + '/2');
  }

  getWeather(lat: number, long: number) {
    let endpoint = `https://api.open-meteo.com/v1/forecast?latitude=${lat}&longitude=${long}&current=temperature_2m,wind_speed_10m&hourly=temperature_2m,relative_humidity_2m,wind_speed_10m`;

    return this.http.get<any>(endpoint);
  }


}
