import { Component } from '@angular/core';
import { faBurger } from '@fortawesome/free-solid-svg-icons';
import {faPersonHiking} from "@fortawesome/free-solid-svg-icons/faPersonHiking";

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css'],
})
export class HeaderComponent {
  iconName = faPersonHiking;
}
<div class="header-container">
  <a class="naroci" routerLink="/">Naroči</a>
  <a class="narocila" routerLink="/seznam">Seznam naročil</a>
  <a class="dostavljalci" routerLink="/dostavljalci">Dostavljalci</a>
  <fa-icon [icon]="iconName" class="faBurger logo"></fa-icon>
  <div>HIKING BUDDY</div>
  <i class="fa-sharp fa-solid fa-truck"></i>
</div>
